// Name: Time Calculation
// ID: SPtimeCalc
// Description: Blocks for calculating and converting time.
// By: SharkPool <https://github.com/SharkPool-SP>

// Version V.2.0.0

(function (Scratch) {
  "use strict";

  const menuIconURI = "data:image/svg+xml;base64,";
  const blockIconURI = "data:image/svg+xml;base64,";

  class TimeCalc {
    getInfo() {
      return {
        id: "SPtimeCalc",
        name: "Time Calculation",
        color1: "#5b54d0",
        color2: "#373279",
        color3: "#373279",
        menuIconURI,
        blockIconURI,
        blocks: [
          {
            opcode: "calculatetimedurationfromdate",
            blockType: Scratch.BlockType.REPORTER,
            text: "difference between [DATE] to current datetime in [TIME_MENU]",
            arguments: {
              DATE: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "2007-03-14 09:34:00",
              },
              TIME_MENU: {
                type: Scratch.ArgumentType.STRING,
                menu: "Time",
                defaultValue: "hour",
              },
            },
          },
          {
            opcode: "calculatetimedurationfromtime",
            blockType: Scratch.BlockType.REPORTER,
            text: "difference between [START_TIME] to current time in [TIME_MENU]",
            arguments: {
              START_TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "00:00:00",
              },
              TIME_MENU: {
                type: Scratch.ArgumentType.STRING,
                menu: "Time",
                defaultValue: "hour",
              },
            },
          },
          {
            opcode: "calculatetimedurationfromstamp",
            blockType: Scratch.BlockType.REPORTER,
            text: "difference between [START_TIME] and [END_TIME] in [TIME_MENU]",
            arguments: {
              START_TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "2007-03-14 09:34:00",
              },
              END_TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "2016-07-21 15:03:00",
              },
              TIME_MENU: {
                type: Scratch.ArgumentType.STRING,
                menu: "Time",
                defaultValue: "hour",
              },
            },
          },
          {
            opcode: "calculatetimedifference",
            blockType: Scratch.BlockType.REPORTER,
            text: "difference between [START_TIME] and [END_TIME] in [TIME_MENU]",
            arguments: {
              START_TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "00:00",
              },
              END_TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "00:00",
              },
              TIME_MENU: {
                type: Scratch.ArgumentType.STRING,
                menu: "Time",
                defaultValue: "hour",
              },
            },
          },
          {
            opcode: "evaluateTime",
            blockType: Scratch.BlockType.REPORTER,
            text: "[MATH] [START_TIME] and [END_TIME]",
            arguments: {
              START_TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "2023-10-10 00:00:00",
              },
              END_TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "1970-01-01 00:10:00",
              },
              MATH: {
                type: Scratch.ArgumentType.STRING,
                menu: "MathType",
                defaultValue: "add",
              },
            },
          },

          "---",

          {
            opcode: "converttotime",
            blockType: Scratch.BlockType.REPORTER,
            text: "convert [VALUE] seconds to [ROUND] time",
            arguments: {
              VALUE: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: 0,
              },
              ROUND: {
                type: Scratch.ArgumentType.NUMBER,
                menu: "Round",
                defaultValue: "exact",
              },
            },
          },
          {
            opcode: "convertToValue",
            blockType: Scratch.BlockType.REPORTER,
            text: "convert [VALUE] to [TIME]",
            arguments: {
              VALUE: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "1970-01-01 00:00:00",
              },
              TIME: {
                type: Scratch.ArgumentType.STRING,
                menu: "Time",
                defaultValue: "hour",
              },
            },
          },
          {
            opcode: "daysinmonth",
            blockType: Scratch.BlockType.REPORTER,
            text: "number of days in [MONTH] [YEAR]",
            arguments: {
              MONTH: {
                type: Scratch.ArgumentType.STRING,
                menu: "Months",
                defaultValue: "January",
              },
              YEAR: {
                type: Scratch.ArgumentType.NUMBER,
                defaultValue: "2000",
              },
            },
          },

          "---",

          {
            opcode: "countdownReport",
            blockType: Scratch.BlockType.REPORTER,
            text: "countdown to [TIME] in [MENU]",
            arguments: {
              TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "2007-03-14 09:34:00",
              },
              MENU: {
                type: Scratch.ArgumentType.STRING,
                menu: "Time",
                defaultValue: "hour",
              },
            },
          },
          {
            opcode: "countdownBoolean",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "countdown to [TIME] reached?",
            arguments: {
              TIME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "2007-03-14 09:34:00",
              },
            },
          },
        ],
        menus: {
          Time: {
            acceptReporters: true,
            items: ["year", "month", "day", "hour", "minute", "second"],
          },
          MathType: {
            acceptReporters: true,
            items: ["add", "subtract"],
          },
          Round: {
            acceptReporters: true,
            items: ["rounded", "exact"],
          },
          Months: {
            acceptReporters: true,
            items: [
              "January",
              "February",
              "March",
              "April",
              "May",
              "June",
              "July",
              "August",
              "September",
              "October",
              "November",
              "December",
            ],
          },
        },
      };
    }

    calculateTimeDifference(startDate, endDate, timeMenu, abs) {
      let timeDiff = endDate.getTime() - startDate.getTime();
      timeDiff = abs ? Math.abs(timeDiff) : timeDiff * -1;
      switch (timeMenu) {
        case "year":
          return timeDiff / 946080000000;
        case "month":
          return timeDiff / 2592000000;
        case "day":
          return timeDiff / 86400000;
        case "hour":
          return timeDiff / 3600000;
        case "minute":
          return timeDiff / 60000;
        case "second":
          return timeDiff / 1000;
        default:
          return "Invalid Menu Input";
      }
    }

    calculatetimedurationfromdate(args) {
      const dateString = args.DATE ? args.DATE : null;
      const timeMenu = args.TIME_MENU;
      const startDate = new Date(dateString);
      const endDate = new Date();
      const difference = this.calculateTimeDifference(
        startDate,
        endDate,
        timeMenu,
        true
      );
      if (isNaN(difference)) {
        return "Invalid Time Input";
      } else {
        return difference;
      }
    }

    calculatetimedurationfromstamp(args) {
      const dateString = args.START_TIME ? args.START_TIME : null;
      const endDateString = args.END_TIME ? args.END_TIME : null;
      const timeMenu = args.TIME_MENU;
      const startDate = new Date(dateString);
      const endDate = new Date(endDateString);
      const difference = this.calculateTimeDifference(
        startDate,
        endDate,
        timeMenu,
        true
      );
      if (isNaN(difference)) {
        return "Invalid Time Input";
      } else {
        return difference;
      }
    }

    calculatetimedurationfromtime(args) {
      const startTime = args.START_TIME ? args.START_TIME : null;
      const timeMenu = args.TIME_MENU;
      const [startHour, startMinute] = startTime.split(":");
      const startDate = new Date();
      startDate.setHours(parseInt(startHour), parseInt(startMinute), 0, 0);
      const endDate = new Date();
      const difference = this.calculateTimeDifference(
        startDate,
        endDate,
        timeMenu,
        true
      );

      if (isNaN(difference)) {
        return "Invalid Time Input";
      } else {
        return difference;
      }
    }

    calculatetimedifference(args) {
      const startTime = args.START_TIME ? args.START_TIME : null;
      const endTime = args.END_TIME ? args.END_TIME : null;
      const timeMenu = args.TIME_MENU;
      const startDate = new Date();
      const endDate = new Date();
      const startHour = parseInt(startTime.split(":")[0]);
      const startMinute = parseInt(startTime.split(":")[1]);
      const endHour = parseInt(endTime.split(":")[0]);
      const endMinute = parseInt(endTime.split(":")[1]);
      startDate.setHours(startHour, startMinute, 0, 0);
      endDate.setHours(endHour, endMinute, 0, 0);

      const difference = this.calculateTimeDifference(
        startDate,
        endDate,
        timeMenu,
        true
      );
      if (isNaN(difference)) {
        return "Invalid Time Input";
      } else {
        return difference;
      }
    }

    converttotime(args) {
      const timestamp = args.VALUE ? args.VALUE : 0;
      const seconds =
        args.ROUND === "rounded" ? Math.round(timestamp % 60) : timestamp % 60;
      const minutes = Math.round((timestamp / 60) % 60);
      const hours = Math.round((timestamp / 3600) % 24);

      const timeString = `${hours}:${minutes}:${seconds}`;
      return timeString;
    }

    convertToValue(args) {
      const dateString = args.VALUE ? args.VALUE : null;
      let date = new Date(dateString);
      date = Math.round(date.getTime() / 1000) - 28800;

      switch (args.TIME) {
        case "year":
          return date / 31536000;
        case "month":
          return date / 2592000;
        case "day":
          return date / 86400;
        case "hour":
          return date / 3600;
        case "minute":
          return date / 60;
        case "second":
          return date;
        default:
          return "Invalid Menu Input";
      }
    }

    daysinmonth(args) {
      const month = args.MONTH;
      const year = args.YEAR ? args.YEAR : null;
      const monthIndex = this._getMonthIndex(month);

      if (monthIndex === -1) {
        return "Invalid month";
      }

      const date = new Date(year, monthIndex + 1, 0);
      return date.getDate();
    }

    _getMonthIndex(month) {
      const months = [
        "January",
        "February",
        "March",
        "April",
        "May",
        "June",
        "July",
        "August",
        "September",
        "October",
        "November",
        "December",
      ];
      return months.indexOf(month);
    }

    countdownReport(args) {
      const timeMenu = args.MENU;
      const endDate = new Date();
      const dateString = args.TIME ? args.TIME : null;
      const startDate = new Date(dateString);
      const difference = this.calculateTimeDifference(
        startDate,
        endDate,
        timeMenu,
        false
      );
      if (isNaN(difference)) {
        return "Invalid Time Input";
      } else {
        return difference;
      }
    }

    countdownBoolean(args) {
      const endDate = Date.now();
      const dateString = args.TIME ? args.TIME : null;
      const startDate = new Date(dateString);
      const timeDifference = startDate - endDate;
      return timeDifference <= 0;
    }

    evaluateTime(args) {
      const startTimeString = args.START_TIME ? args.START_TIME : null;
      const endTimeString = args.END_TIME ? args.END_TIME : null;
      const startDate = new Date(startTimeString);
      const endDate = new Date(endTimeString);

      if (isNaN(startDate) || isNaN(endDate)) {
        return "Invalid Time Input";
      }

      let timeDifference = "";
      if (args.MATH === "add") {
        timeDifference = startDate.getTime() + endDate.getTime();
      } else {
        timeDifference = startDate.getTime() - endDate.getTime();
      }
      const resultDate = new Date(timeDifference);
      const year = resultDate.getFullYear();
      const month = String(resultDate.getMonth() + 1).padStart(2, "0");
      const day = String(resultDate.getDate()).padStart(2, "0");
      const changer = args.MATH === "add" ? 8 : -8;
      const hours = String(resultDate.getHours() - changer).padStart(2, "0");
      const minutes = String(resultDate.getMinutes()).padStart(2, "0");
      const seconds = String(resultDate.getSeconds()).padStart(2, "0");

      const formattedResult = `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
      return formattedResult;
    }
  }
  Scratch.extensions.register(new TimeCalc());
})(Scratch);
